class GameInfoBlock extends Component {

    state = {
        gameInfo: this.props.gameInfo
    }

    componentDidMount(){
        //const { handle } = this.props.match.params
        gameInfo = this.props.gameInfo
    }
    render() {
        return (
            <div className="game-info container">
                <h1>{gameInfo.name}</h1>
                {/* <img>Game Image</img> */}
                <h3>Game Rating</h3>
                <p>Game Summary</p>
                {/* <img>Link Icon</img> */}
                <a>Link</a>
            </div>
        );
    }
}

export default GameInfoBlock;
