import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Axios from 'axios';

const appid = require("appid");
class WishlistCard extends Component {
    state = {
        games: [],
        gameInfo: {
            name: "",
            bestPrice: "",
            seller: "",
            page: ""
        },
        gameComp:[],
        steamGame: [],
        gogGame: [],
        user: []

    }

    async componentWillMount(){
        const email = localStorage.getItem("loggedInUser");
        const params = {
             email: email
        }
        console.log({params})
        Axios.get("http://localhost:8080/getID", {params})
        .then( response =>{
            this.setState({
                user: response.data
            })
            console.log(this.state.user);
            console.log(this.state.user.id);
            const userID = this.state.user.id;
            const userIDObj = {
                userID: userID
            }
            console.log({userIDObj})
            Axios.get('http://localhost:8080/findAllGames', `${userIDObj}`)
            .then(response => {
                this.setState({
                    games: response.data
                })
                for(let i = 0; i < this.state.games.length; i++){
                    this.state.gameInfo["name"] = this.state.games[i].name;
                    console.log(this.state.gameInfo);
                    let dota = this.getApiID(`${this.state.games[i].name}`) 
                    console.log(dota);
                    //await appid(`${this.state.games[i].name}`);
                    Axios.get(`https://cors-anywhere.herokuapp.com/https://store.steampowered.com/api/appdetails/?appids=${dota.appid}`)
                    .then( response => {
                        const steamGame = response.data[dota.appid].data
                        // steamGame.price_overview.final/100
                        this.setState({
                            steamGame: steamGame
                        })
                        Axios.get(`https://cors-anywhere.herokuapp.com/http://embed.gog.com/games/ajax/filtered?mediaType=game&search=${this.state.games[i].name}`)
                        .then(response =>{
                            for(let j = 0; j < response.data.products.length; j++){
                                if(response.data.products[j].title == this.state.games[i].name){
                                    const gogGame = response.data.products[j]
                                    //.price.finalAmount
                                    
                                    this.setState({
                                        gogGame: gogGame
                                    })
                                }
                            }
                            console.log(this.state.gogGame);
                            console.log(this.state.steamGame);
                            if(this.state.steamGame.price_overview.final/100 >= this.state.gogP.price.finalAmount){
                                const tempState = {...this.state.gameInfo}
                                tempState["bestPrice"] = this.state.steamGame.price_overview.final/100;
                                tempState["Seller"] = "Steam";
                                tempState["page"] = `https://store.steampowered.com/app/${this.state.steamGame.steam_appid}`
                                this.setState({
                                    gameInfo: tempState
                                })
                                console.log(this.state.gameInfo, "End of if price check statement");
                            }
                            else{
                                const tempState = {...this.state.gameInfo}
                                tempState["bestPrice"] = this.state.gogGame.price.finalAmount;
                                tempState["Seller"] = "Gog";
                                tempState["page"] = `https://www.gog.com${this.state.gogGame.url}`
                                this.setState({
                                    gameInfo: tempState
                                })
                                console.log(this.state.gameInfo, "End of else price check statement");
                            }
                            this.state.gameComp.push(this.state.gameInfo);
                            console.log(this.state.gameComp);
                        })
                        
                    })
                    
                }
                //console.log(this.state.gameComp);
            })
            
        })
        
    }

    async getApiID(game){
        return await appid(`${game}`);
    }
    render() {
        return (
            this.state.gameComp.map ((gameComp, index) => {
                return(
                    <div className="Card">
                        <h3>{gameComp.gameInfo.name}</h3>
                        <h5>{gameComp.gameInfo.seller}</h5>
                        {gameComp.gameInfo.bestPrice}
                        <a href={gameComp.gameInfo.page} target="_blank">{gameComp.gameInfo.name}</a>
                    </div>
                )
            })

            
        );
    }
}

export default WishlistCard;
