import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Axios from 'axios';

const appid = require("appid");
class WishlistCard extends Component {
    state = {
        games: [],
        gameInfo: {
            name: "",
            bestPrice: "",
            seller: "",
            page: ""
        },
        steamP: 0.00,
        gogP: 0.00
    }

    async componentWillMount(){
        Axios.get()
        .then(response => {
            this.setState({
                games: response.data
            })
        })
        for(i = 0; i < this.state.games.length; i++){
            let dota = await appid(`${this.props.location.state.gameName.name}`);
            Axios.post(`https://cors-anywhere.herokuapp.com/https://store.steampowered.com/api/appdetails/?appids=${dota.appid}`)
            .then( response => {
                const price = response.data[dotaID].data.price_overview.final/100
                this.setState({
                    steamP: price
                })
            })
        }
        
    }
    render() {
        return (
            this.state.games.map ((games, index) => {
                <div className="Card">
                <h3>Name of Game</h3>
                <h5>Name of Company</h5>
                 Best Price
                <Link>Game Page</Link>
            </div>
            })
            
        );
    }
}

export default WishlistCard;
