import React, { Component } from 'react';
import Axios from 'axios';
class GameInfoBlock extends Component {

    state = {
        gameInfo: this.props.gameInfo,
        image: ""
    }
    componentDidMount(){
        Axios({
            url: "https://cors-anywhere.herokuapp.com/https://api-v3.igdb.com/covers",
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'user-key': '9228051aa3584afc60ae39330497be33'
            },
            data: `fields image_id; where id = ${this.state.gameInfo.cover};`
            
        })
        .then(response => {
            
            console.log(this.state.gameInfo);
            console.log(response.data);
            console.log(response.data[0].image_id);
            this.setState({
                image: `https://images.igdb.com/igdb/image/upload/t_screenshot_big/${response.data[0].image_id}.jpg`
            })
            
        })
    }
    render() {
        return (
            <div className="">
                <div className="jumbotron jumbotron-fluid">
                    <img src={this.state.image}  width="100%" height="300px" className="imgPic"></img>
                    </div>
                <div className="game-info container">
                    <img src={this.state.image} class="w3-square" alt="Avatar" classname="game-picture"/>
                    <h1>{this.state.gameInfo.name}</h1>
                    {/* <img>Game Image</img> */}
                    <h3>Rating: {this.state.gameInfo.rating}</h3>
                    <p>{this.state.gameInfo.summary}</p>
                    {/* <img>Link Icon</img> */}
                    <a>Link</a>
                </div>
            </div>
        );
    }
}

export default GameInfoBlock;
