import React, { Component } from 'react';
import Axios from 'axios';
class GameInfoBlock extends Component {

    state = {
        gameInfo: this.props.gameInfo,
        image: ""
    }
    componentDidMount(){
        Axios({
            url: "https://cors-anywhere.herokuapp.com/https://api-v3.igdb.com/games",
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'user-key': '9228051aa3584afc60ae39330497be33'
            },
            data: `fields name; where id = ${this.state.gameInfo.id};`
            
        })
        .then(response => {
            
            
            console.log(response.data);
            this.setState({
                image: response.data.name
            })
            console.log(response.data.name);
        })
    }
    render() {
        console.log(image);
        return (
            
            <div className="game-info container">
                <img src={this.state.image} class="w3-circle" alt="Avatar"/>
                <h1>{this.state.gameInfo.name}</h1>
                {/* <img>Game Image</img> */}
                <h3>Rating: {this.state.gameInfo.rating}</h3>
                <p>{this.state.gameInfo.summary}</p>
                {/* <img>Link Icon</img> */}
                <a>Link</a>
            </div>
        );
    }
}

export default GameInfoBlock;
