import React, { Component } from 'react';
import SearchBar from './searchbar/SearchBar';
import WishlistCard from './wishlist/WishlistCard';
import NewsCard from './newsInfo/NewsCard';
import SignUp from './../signup/SignUp';
import { Link } from 'react-router-dom';
import Axios from 'axios';

const express = require('express')
const cors = require('cors')
const app = express()
 
app.use(cors())
class Home extends Component {



    submitHandler = (event) =>{
        event.preventDefault();
        console.log("hello world");
        app.post('http://localhost:8080/submitWishList', "Grim Dawn", 24.99, localStorage.getItem("loggedInUser"))
        .then(response=>{
            console.log("hello world 1.0");
            this.props.history.push('/home');
        })
    }

    render() {

        let signedIn = (
                <SignUp {...this.props}/>
        )
        if(localStorage.getItem("loggedInUser")){
            signedIn = (
                <ul className="wishList">
                    <WishlistCard/>
                    <WishlistCard/>
                    <WishlistCard/>
                    <WishlistCard/>
                    <WishlistCard/>
                    <WishlistCard/>
                </ul>
            )
        }
        return (
            <div>
                <SearchBar/>
                <br/>
                <br/>
                <br/>
                <form onSubmit={this.submitHandler}>
                <button  className="btn btn-outline-success" type="submit" >Button</button>
                </form>
                {signedIn}
            </div>
        );
    }
}

export default Home;
