import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Axios from 'axios';

const appid = require("appid");
class WishlistCard extends Component {
    state = {
        games: [],
        gameInfo: {
            name: "",
            bestPrice: "",
            seller: "",
            page: ""
        },
        gameComp:[],
        steamGame: [],
        gogGame: [],
        user: []

    }
    getData(gameCall){
        return new Promise((resolve, reject) => {
            Axios.get(`${gameCall}`)
            .then((data) => {
                resolve(data)
                console.log(data);
                //this.setState({steamGame: data})
            })
        })
    }
    componentDidMount(){
        const email = localStorage.getItem("loggedInUser");
        const params = {
             email: email
        }
        // this.getData(`https://cors-anywhere.herokuapp.com/http://embed.gog.com/games/ajax/filtered?mediaType=game&search=Grim Dawn`);
        // this.getData(`https://cors-anywhere.herokuapp.com/https://store.steampowered.com/api/appdetails/?appids=219990`);
        console.log(email);
        console.log({params});
        Axios.get("http://localhost:8080/getID", {params})
        .then( response =>{
            console.log("changing state");
            this.setState({
                user: response.data
            })
            console.log(this.state.user);
            console.log(this.state.user.id);
            const userID = this.state.user.id;
            console.log(userID);
            const userIDObj = {
                userID: userID
            }
            
            console.log({userIDObj});
            Axios.get(`http://localhost:8080/userGames?userID=${userID}`)
            .then(response => {
                this.setState({
                    games: response.data
            })
            this.getGames();
            })
            
        })
        
    }
    async getGames(){
        for(let i = 0; i < this.state.games.length; i++){
            let dota = await appid(`${this.state.games[i].name}`);
            console.log(dota);
            //await this.getData(`https://cors-anywhere.herokuapp.com/https://store.steampowered.com/api/appdetails/?appids=${dota.appid}`);
            // this.setState({steamGame: await this.getData(`https://cors-anywhere.herokuapp.com/https://store.steampowered.com/api/appdetails/?appids=${dota.appid}`)})
            
            console.log(this.state.steamGame);
        }
    }
    async getApiID(){
        console.log(this.state.games.length);
        for(let i = 0; i < this.state.games.length; i++){
            console.log(this.state.games.length, "inside of for statement");
            this.state.gameInfo["name"] = this.state.games[i].name;
            console.log(this.state.gameInfo);
            let dota = await appid(`${this.state.games[i].name}`);
            console.log(dota);
            //
            this.setState({steamGame: await this.getData(`https://cors-anywhere.herokuapp.com/https://store.steampowered.com/api/appdetails/?appids=${dota.appid}`)})
            this.setState({gogGame: await this.getData(`https://cors-anywhere.herokuapp.com/http://embed.gog.com/games/ajax/filtered?mediaType=game&search=${this.state.games[i].name}`)})
            console.log(steamGame);
            console.log(gogGame);
            for(let j = 0; j < this.state.gogGame.length; j++){
                if(this.state.gogGame.products[j].title == this.state.games[i].name){
                    this.setState({gogGame: this.state.gogGame.products[j]})
                    console.log(this.state.gogGame);
                }
            }








            Axios.get(`https://cors-anywhere.herokuapp.com/https://store.steampowered.com/api/appdetails/?appids=${dota.appid}`)
            .then( response => {
                const steamGame = response.data[dota.appid].data
                // steamGame.price_overview.final/100
                this.setState({
                    steamGame: steamGame
                })
                Axios.get(`https://cors-anywhere.herokuapp.com/http://embed.gog.com/games/ajax/filtered?mediaType=game&search=${this.state.games[i].name}`)
                .then(response =>{
                    for(let j = 0; j < response.data.products.length; j++){
                        if(response.data.products[j].title == this.state.games[i].name){
                            const gogGame = response.data.products[j]
                            //.price.finalAmount
                            
                            this.setState({
                                gogGame: gogGame
                            })
                        }
                    }
                    console.log(this.state.gogGame);
                    console.log(this.state.steamGame);
                    console.log(this.state.steamGame.price_overview.final/100);
                    if(this.state.steamGame.price_overview.final/100 <= this.state.gogGame.price.finalAmount){
                        const tempState = {...this.state.gameInfo}
                        tempState["bestPrice"] = this.state.steamGame.price_overview.final/100;
                        tempState["seller"] = "Steam";
                        tempState["page"] = `https://store.steampowered.com/app/${this.state.steamGame.steam_appid}`
                        tempState["name"] = this.state.steamGame.name;
                        this.setState({
                            gameInfo: tempState
                        })
                        console.log(this.state.gameInfo, "End of if price check statement");
                    }
                    else{
                        const tempState = {...this.state.gameInfo}
                        tempState["bestPrice"] = this.state.gogGame.price.finalAmount;
                        tempState["seller"] = "Gog";
                        tempState["page"] = `https://www.gog.com${this.state.gogGame.url}`
                        tempState["name"] = this.state.gogGame.title;
                        this.setState({
                            gameInfo: tempState
                        })
                        console.log(this.state.gameInfo, "End of else price check statement");
                    }
                    let temp = this.state.gameComp
                    temp.push(this.state.gameInfo)
                    this.setState({gameComp: temp})
                })
                
            })
            
        }
    }
    render() {
        return (
            
            this.state.gameComp.map ((gameComp, index) => {
                console.log("in map :>> ",gameComp.name);
                return(
                    <div className="Card">
                        <h3>{gameComp.name}</h3>
                        <h5>{gameComp.seller}</h5>
                        {gameComp.bestPrice}
                        <a href={gameComp.page} target="_blank">{gameComp.name}</a>
                    </div>
                )
            })

            
        );
    }
}

export default WishlistCard;
