import React, { Component } from 'react';
import Axios from 'axios';


//const { API_KEY } = '29E2F9718C66A7C0545E3C6882ABE0EA';
const { API } = 'https://api.steampowered.com/ISteamApps/GetAppList/v2/?key=29E2F9718C66A7C0545E3C6882ABE0EA';
class SearchBar extends Component {

    state={
        search:{
            isLoaded: false,
            items: []
        }
    }

    componentDidMount(){
        Axios.get('https://cors-anywhere.herokuapp.com/https://api.steampowered.com/ISteamApps/GetAppList/v2/')
        .then(response => {
            (result) =>{
                console.log(response);
            }
        });
    }

    render() {
        return (
            <div className="w3-container">
  
                <form className="searchBar">
                    <input type="text" placeholder="Game Name" ref={input => this.search = input} onChange={this.handleInputChange} name="search"/>
                    <button type="submit"><i className="fa fa-search"></i></button>
                    
                </form>
            </div>
        );
    }
}

export default SearchBar;
