import React, { Component } from 'react';
import {Link} from 'react-router-dom';

class Header extends Component {


    state={
        user:{
            email: '',
            passsword: ''
        }
    }

    signOut = ()=>{
        localStorage.removeItem("loggedInUser");
        this.props.history.push("/");
    }



    render() {
        let signIn = (
            <form onSubmit={this.submitHandler} className="form-inline mt-2 mt-md-0">
                <button className="w3-bar-item w3-button w3-hide-small w3-right w3-hover-white" type="submit">Sign In</button>
                <input onChange={this.handleChange} value={this.state.email} name="email" className=" w3-hide-small w3-right  w3-hover-white" type="email" placeholder="Email" aria-label="Search" />
                <input onChange={this.handleChange} value={this.state.password} name="password" className="w3-hide-small w3-right w3-hover-white" type="password" placeholder="Password" aria-label="Search" />
                {/* <button className="w3-bar-item w3-button w3-hide-small w3-right w3-padding-small w3-hover-white" type="submit">Sign In</button> */}
            </form>
        )
        if(localStorage.getItem("loggedInUser") == false){
            signIn = (
                <Link href="#" class="w3-bar-item w3-button w3-hide-small w3-right w3-padding-large w3-hover-white" title="My Account">
                    <img src="" class="w3-circle" style={{height:'23px;width:23px'}} alt="Avatar"/>
                </Link>
            )
        }
        return (
            <div className="w3-top">
                <div className="w3-bar w3-theme-d2 w3-left-align w3-large">
                <Link className="w3-bar-item w3-button w3-hide-medium w3-hide-large w3-right w3-padding-large w3-hover-white w3-large w3-theme-d2"><i className="fa fa-bars"></i></Link>
                <Link  className="w3-bar-item w3-button w3-padding-large w3-theme-d4">Logo</Link>
                <Link  className="w3-bar-item w3-button w3-hide-small w3-padding-large w3-hover-white">News</Link>
                <Link  className="w3-bar-item w3-button w3-hide-small w3-padding-large w3-hover-white">Account Settings</Link>
                <Link  className="w3-bar-item w3-button w3-hide-small w3-padding-large w3-hover-white">Messages</Link>
                <div className="w3-dropdown-hover w3-hide-small">
                    <button className="w3-button w3-padding-large" title="Notifications"><i className="fa fa-bell"></i><span className="w3-badge w3-right w3-small w3-green">3</span></button>     
                    <div className="w3-dropdown-content w3-card-4 w3-bar-block" style={{width:'300px'}}>
                        <Link href="#" className="w3-bar-item w3-button">One new friend request</Link>
                        <Link href="#" className="w3-bar-item w3-button">John Doe posted on your wall</Link>
                        <Link href="#" className="w3-bar-item w3-button">Jane likes your post</Link>
                    </div>
                </div>
                    {signIn}
                </div>
            </div>
        );
    }
}

export default Header;
