import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import Axios from 'axios';

class Header extends Component {


    state={
        user:{
            email: '',
            passsword: ''
        }
    }

    signOut = ()=>{
        localStorage.removeItem("loggedInUser");
        this.props.history.push("/home");
    }

    handleChange = (event)=>{
        const value = event.target.value;
        const name = event.target.name;
        const tempUser= {...this.state.user}
        tempUser[name] = value;
        this.setState
        (
            {
                user:tempUser
            }
        )
    }

    submitHandler = (event)=>{
        event.preventDefault();

        Axios.post('http://localhost:8080/loggingIn', this.state.user)
        .then(response=>{
            localStorage.setItem('loggedInUser', response.data.email);
            this.props.history.push('/home');
        })
    }


    render() {
        let signIn = (
            <form onSubmit={this.submitHandler} className="form-inline w3-right mt-2 mt-md-0">
                <button className="w3-bar-item w3-button w3-hide-small w3-right w3-padding-small w3-hover-white" type="submit">Sign In</button>
                <input onChange={this.handleChange} value={this.state.email} name="email" className=" w3-hide-small w3-right  w3-hover-white" type="email" placeholder="Email" aria-label="Search" />
                <input onChange={this.handleChange} value={this.state.password} name="password" className="w3-hide-small w3-right w3-hover-white" type="password" placeholder="Password" aria-label="Search" />
                {/* <button className="w3-bar-item w3-button w3-hide-small w3-right w3-padding-small w3-hover-white" type="submit">Sign In</button> */}
            </form>
        )
        if(localStorage.getItem("loggedInUser")){
            signIn = (
                <div>
                    <Link to="/account" class="w3-bar-item w3-button w3-hide-small w3-right w3-padding-large w3-hover-white" title="My Account">
                        <img src="" class="w3-circle" style={{height:'23px;width:23px'}} alt="Avatar"/>
                    </Link>
                    <button onClick={this.signOut} className="btn btn-outline-success my-2 my-sm-0 w3-right " type="submit">Sign Out</button>
                </div>
                
            )
        }
        return (
            <div className="w3-top">
                <div className="w3-bar w3-theme-d2 w3-left-align w3-large">
                    <Link className="w3-bar-item w3-button w3-hide-medium w3-hide-large w3-right w3-padding-large w3-hover-white w3-large w3-theme-d2"><i className="fa fa-bars"></i></Link>
                    <Link  className="w3-bar-item w3-button w3-padding-large w3-theme-d4" to="/home">Logo</Link>
                    <Link  className="w3-bar-item w3-button w3-hide-small w3-padding-large w3-hover-white">News</Link>
                    <Link  className="w3-bar-item w3-button w3-hide-small w3-padding-large w3-hover-white" to="/account">Account Settings</Link>
                    {signIn}
                </div>
            </div>
        );
    }
}

export default Header;
