import React, { Component } from 'react';
import Axios from 'axios';
import Suggestions from './Suggestions/suggestions';


const { API_KEY } = process.env
const API_URL = 'https://api.steampowered.com/ISteamApps/GetAppList/v2/'

class SearchBar extends Component {

    state = {
        query: '',
        results: []
    }

    async componentDidMount(){
        Axios.get("https://api.steampowered.com/ISteamApps/GetAppList/v2")
        .then(response => {
            this.setState({query: response.data.name})
            console.log(response.data.name);
        })
    }
    handleInputChange = () => {
        this.setState({
          query: this.search.value
        }, () => {
          if (this.state.query && this.state.query.length > 1) {
            if (this.state.query.length % 2 === 0) {
              this.getInfo()
            }
          } else if (!this.state.query) {
          }
        })
      }

    getInfo = () => {
        Axios.get(`${API_URL}?_limit=7`)
          .then(({ data }) => {
            this.setState({
              results: data.data // MusicGraph returns an object named data, 
                                 // as does axios. So... data.data                             
            })
          })
      }

    render() {
        return (
            <div className="w3-container">
  
                <form className="searchBar">
                    <input type="text" placeholder="Game Name" ref={input => this.search = input} onChange={this.handleInputChange} name="search"/>
                    <button type="submit"><i class="fa fa-search"></i></button>
                    <Suggestions results={this.state.results}/>
                </form>
            </div>
        );
    }
}

export default SearchBar;
