import React from 'react';

const lengthLimit = 7;
let lengthChecker =0;
const Suggestions = (props) => {
    const options  = props.items.map(r =>(
        
        <li key={r.id}>
            {r.name}
        </li>
    ));
    if(lengthLimit <= lengthChecker){
        lengthChecker ++;
        return <ul>{options}</ul>
    }
    else if ( lengthChecker >= 7){
        lengthChecker = 0;
        return <ul></ul>;
    }
}

export default Suggestions
