import React, { Component } from 'react';
import Axios from 'axios';
class GameInfoBlock extends Component {

    state = {
        gameInfo: this.props.gameInfo,
        image: ""
    }
    componentDidMount(){
        Axios({
            url: "https://cors-anywhere.herokuapp.com/https://api-v3.igdb.com/covers",
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'user-key': '9228051aa3584afc60ae39330497be33'
            },
            data: `fields *; where id = ${this.state.gameInfo.cover};`
            
        })
        .then(response => {
            
            
            console.log(response.data[0].url);
            this.setState({
                image: response.data[0].url
            })
            console.log(response.data);
        })
    }
    render() {
        return (
            <div className="">
                <div className="jumbotron" height="40%">
                    <h1>Bootstrap Tutorial</h1>
                    <p>Bootstrap is the most popular HTML, CSS, and JS framework for developing responsive,
                    mobile-first projects on the web.</p>
                    </div>
                <div className="game-info container">
                    <img src={this.state.image} class="w3-square" alt="Avatar" classname="game-picture"/>
                    <h1>{this.state.gameInfo.name}</h1>
                    {/* <img>Game Image</img> */}
                    <h3>Rating: {this.state.gameInfo.rating}</h3>
                    <p>{this.state.gameInfo.summary}</p>
                    {/* <img>Link Icon</img> */}
                    <a>Link</a>
                </div>
            </div>
        );
    }
}

export default GameInfoBlock;
